mapper.insert:
	 
	insert into Board (
		board_num
		, board_title
		, board_contents
		, person_id

	) values (
		board_number_sequence.nextval
		, #{board_title}
		, #{board_contents}
		, #{person_id}
	)

mapper.delete:

	delete
		from 
			Board
	where board_num = #{board_num}
	and person_id = #{person_id}

mapper.update:
update Board
set 
	board_title = #{board_title}
	, board_contents = #{board_contents}
where
	board_num = #{board_num}
	and person_id = #{person_id}
